import { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import api from "../services/api";
import Navbar from "../components/Navbar";
import TaskItem from "../components/TaskItem";

export default function Dashboard() {
  const { token, logout } = useContext(AuthContext);
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [search, setSearch] = useState("");
  const [user, setUser] = useState(null);

  const headers = { Authorization: `Bearer ${token}` };

  // Load tasks and user profile
  const loadData = async () => {
    try {
      const userRes = await api.get("/auth/profile", { headers });
      setUser(userRes.data);

      const taskRes = await api.get("/tasks", { headers });
      setTasks(taskRes.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  const addTask = async () => {
    if (!title.trim()) return;
    const res = await api.post("/tasks", { title }, { headers });
    setTasks([...tasks, res.data]);
    setTitle("");
  };

  const updateTask = async (id, updatedTitle) => {
    const res = await api.put(`/tasks/${id}`, { title: updatedTitle }, { headers });
    setTasks(tasks.map(t => t._id === id ? res.data : t));
  };

  const delTask = async (id) => {
    await api.delete(`/tasks/${id}`, { headers });
    setTasks(tasks.filter(t => t._id !== id));
  };

  const filteredTasks = tasks.filter(t => t.title.toLowerCase().includes(search.toLowerCase()));

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-blue-400 to-indigo-600">
      <Navbar />

      <div className="flex flex-col items-center p-10">
        {user && (
          <div className="mb-6 bg-white rounded-xl shadow-lg p-4 w-full max-w-md text-center">
            <h2 className="font-bold text-xl">{user.fullName}</h2>
            <p className="text-gray-600">{user.email}</p>
            <button 
              className="mt-2 px-4 py-1 bg-red-500 text-white rounded hover:bg-red-600"
              onClick={logout}
            >Logout</button>
          </div>
        )}

        <h1 className="text-4xl font-bold text-white mb-6 drop-shadow-lg">Habit Tracker</h1>

        <div className="flex gap-2 w-full max-w-md mb-4">
          <input
            className="flex-grow px-4 py-2 rounded-l-xl border-2 border-white focus:outline-none focus:ring-2 focus:ring-white"
            placeholder="Add New Task"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <button
            className="bg-white text-blue-600 font-semibold px-6 rounded-r-xl hover:bg-gray-100 transition"
            onClick={addTask}
          >Add</button>
        </div>

        <input
          type="text"
          placeholder="Search tasks..."
          className="w-full max-w-md px-4 py-2 rounded-xl mb-4 focus:outline-none focus:ring-2 focus:ring-white border-2 border-white"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />

        <div className="w-full max-w-md space-y-2">
          {filteredTasks.map((t) => (
            <TaskItem 
              key={t._id} 
              task={t} 
              onDelete={delTask} 
              onUpdate={updateTask} 
            />
          ))}
        </div>
      </div>
    </div>
  );
}
