import { useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate, Link } from "react-router-dom";
import api from "../services/api";

export default function Login() {
  const { login } = useContext(AuthContext);
  const nav = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const submit = async () => {
    try {
      const res = await api.post("/auth/login", { email, password });
      login(res.data.token);
      nav("/dashboard");
    } catch {
      alert("Invalid login");
    }
  };

  return (
    <div className="p-10 flex flex-col gap-3 w-80 mx-auto mt-20">
      <h1 className="text-xl font-bold">Login</h1>

      <input className="border p-2" placeholder="Email"
        onChange={(e) => setEmail(e.target.value)} />

      <input className="border p-2" placeholder="Password" type="password"
        onChange={(e) => setPassword(e.target.value)} />

      <button className="bg-blue-600 text-white p-2" onClick={submit}>
        Login
      </button>

      <Link to="/register" className="text-blue-600 underline">Create account</Link>
    </div>
  );
}
