import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import api from "../services/api";

export default function Register() {
  const nav = useNavigate();

  const [data, setData] = useState({
    name: "", email: "", password: ""
  });

  const submit = async () => {
    await api.post("/auth/register", data);
    alert("Account created!");
    nav("/");
  };

  return (
    <div className="p-10 flex flex-col gap-3 w-80 mx-auto mt-20">
      <h1 className="text-xl font-bold">Register</h1>

      <input className="border p-2" placeholder="Name"
        onChange={(e) => setData({ ...data, name: e.target.value })} />

      <input className="border p-2" placeholder="Email"
        onChange={(e) => setData({ ...data, email: e.target.value })} />

      <input className="border p-2" placeholder="Password" type="password"
        onChange={(e) => setData({ ...data, password: e.target.value })} />

      <button className="bg-green-600 text-white p-2" onClick={submit}>
        Register
      </button>

      <Link to="/" className="text-blue-600 underline">Already have an account?</Link>
    </div>
  );
}
