import { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import api from "../services/api";
import Navbar from "../components/Navbar";
import TaskItem from "../components/TaskItem";
import { User } from "lucide-react"; // profile icon

export default function Dashboard() {
  const { token, logout } = useContext(AuthContext);
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [search, setSearch] = useState("");
  const [user, setUser] = useState(null);
  const [showProfile, setShowProfile] = useState(false);

  const headers = { Authorization: `Bearer ${token}` };

  const loadData = async () => {
    try {
      const userRes = await api.get("/auth/profile", { headers });
      setUser(userRes.data);

      const taskRes = await api.get("/tasks", { headers });
      setTasks(taskRes.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  const addTask = async () => {
    if (!title.trim()) return;
    const res = await api.post("/tasks", { title }, { headers });
    setTasks([...tasks, res.data]);
    setTitle("");
  };

  const updateTask = async (id, updatedTitle) => {
    const res = await api.put(`/tasks/${id}`, { title: updatedTitle }, { headers });
    setTasks(tasks.map(t => t._id === id ? res.data : t));
  };

  const delTask = async (id) => {
    await api.delete(`/tasks/${id}`, { headers });
    setTasks(tasks.filter(t => t._id !== id));
  };

  const filteredTasks = tasks.filter(t => t.title.toLowerCase().includes(search.toLowerCase()));

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-blue-400 to-indigo-600">
      <Navbar />

      <div className="flex flex-col items-center p-10">
        <div className="flex items-center justify-end w-full max-w-md mb-6">
          <button onClick={() => setShowProfile(!showProfile)} className="text-white">
            <User size={28} />
          </button>
        </div>

        {showProfile && user && (
          <div className="mb-6 bg-white rounded-xl shadow-lg p-6 w-full max-w-md text-center">
            <h2 className="font-bold text-xl mb-1">{user.fullName}</h2>
            <p className="text-gray-600 mb-1">Username: {user.username}</p>
            <p className="text-gray-600 mb-1">Email: {user.email}</p>
            <p className="text-gray-600 mb-1">Phone: {user.phone}</p>
            <p className="text-gray-600 mb-1">Gender: {user.gender}</p>
            <p className="text-gray-600 mb-2">DOB: {user.dob}</p>
            <button 
              className="mt-2 px-4 py-1 bg-red-500 text-white rounded hover:bg-red-600 transition"
              onClick={logout}
            >Logout</button>
          </div>
        )}

        <h1 className="text-4xl font-bold text-white mb-8 drop-shadow-lg">Habit Tracker</h1>

        <div className="flex gap-2 w-full max-w-md mb-4">
          <input
            className="flex-grow px-4 py-3 rounded-l-xl border-2 border-white focus:outline-none focus:ring-2 focus:ring-white shadow-lg placeholder-gray-300"
            placeholder="Add New Task"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <button
            className="bg-white text-blue-600 font-semibold px-6 rounded-r-xl hover:bg-gray-100 shadow-lg transition"
            onClick={addTask}
          >Add</button>
        </div>

        <input
          type="text"
          placeholder="Search tasks..."
          className="w-full max-w-md px-4 py-3 rounded-xl mb-6 focus:outline-none focus:ring-2 focus:ring-white border-2 border-white shadow-md placeholder-gray-300"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />

        <div className="w-full max-w-md space-y-3">
          {filteredTasks.map((t) => (
            <TaskItem 
              key={t._id} 
              task={t} 
              onDelete={delTask} 
              onUpdate={updateTask} 
            />
          ))}
        </div>
      </div>
    </div>
  );
}
