export default function TaskItem({ task, onDelete, onUpdate }) {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(task.title);

  const handleUpdate = () => {
    onUpdate(task._id, title);
    setIsEditing(false);
  };

  return (
    <div className="flex items-center justify-between mb-2 bg-white p-2 rounded shadow">
      {isEditing ? (
        <>
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="border p-1 rounded flex-grow"
          />
          <button onClick={handleUpdate} className="bg-green-500 text-white px-2 rounded ml-2">Save</button>
        </>
      ) : (
        <>
          <span>{task.title}</span>
          <div className="flex gap-2">
            <button onClick={() => setIsEditing(true)} className="bg-yellow-400 px-2 rounded">Edit</button>
            <button onClick={() => onDelete(task._id)} className="bg-red-500 text-white px-2 rounded">Delete</button>
          </div>
        </>
      )}
    </div>
  );
}
