export default function TaskItem({ task, onDelete }) {
  return (
    <div className="flex justify-between border p-2 mt-2">
      <span>{task.title}</span>
      <button
        onClick={() => onDelete(task._id)}
        className="bg-red-500 text-white px-2">
        Delete
      </button>
    </div>
  );
}
